mapper.insert:
insert into member
	(
		id, memberid, memberpw, email, phone

			, originalthumbnail,
			thumbnail

	)
values
	(
		member_seq.nextval, #{memberid}, #{memberpw}, #{email}, #{phone}

			, #{originalthumbnail},
			#{thumbnail}

	)

mapper.update:
update
	member
set
	email = #{email}, phone = #{phone}

	, originalthumbnail = #{originalthumbnail},
	thumbnail = #{thumbnail}

where
	memberid = #{memberid}

update member
set last_login = sysdate
where memberid = #{memberid}
