mapper.insert:
insert into RECRUITMENT_POST
(
	id
	, writer_id
	, title
	, content
	, hits
) values (
	RECRUITMENT_POST_SEQ.nextval
	, #{writer_id}
	, #{title}
	, #{content}
	, #{hits}
)

mapper.update:
update
	RECRUITMENT_POST
set
	title = #{title}
	,content = #{content}
where
	id = #{id}
	and writer_id = #{writer_id}
